import styled from "styled-components";
import {
  typography,
  space,
  color,
  layout,
  background,
  border,
  position,
  variant,
} from "styled-system";

export const ButtonPrimary = styled.button`
  border: none;
  background-color: hsla(319, 100%, 57%, 1);
  color: white;
  border-radius: 30px;
  padding: 10px 20px;
  line-height: 18px;
  min-width: 80px;
  ${typography}
  ${space}
  ${color}
  ${layout}
  ${background}
  ${border}
  ${position}
  ${variant}
`;

export const ButtonSecondary = styled.button`
  border: 1px solid grey;
  background-color: transparent;
  color: grey;
  border-radius: 30px;
  padding: 10px 20px;
  min-width: 80px;
  ${typography}
  ${space}
  ${color}
  ${layout}
  ${background}
  ${border}
  ${position}
  ${variant}
`;
